import React from 'react';
import { BrowserRouter as Router, Route, Routes, useLocation } from 'react-router-dom';
import Login from './components/Login';
import Register from './components/Register';
import Quiz from './components/Quiz';
import Landing from './components/Landing';
import Navbar from './components/Navbar';
import AdminDashboard from './components/AdminDashboard'; // Import AdminDashboard

function App() {
    const location = useLocation(); // Hook to get current location
    const token = localStorage.getItem('token');
    let isAdmin = false;

    if (token) {
        const decodedToken = jwtDecode(token);
        isAdmin = decodedToken.role === 'admin'; // Check if the user is an admin
    }

    // Conditionally render Navbar based on the current route
    const showNavbar = location.pathname !== '/quiz';

    return (
        <div>
            {showNavbar && <Navbar />} {/* Only show Navbar if not on /quiz */}
            <Routes>
                <Route path="/" element={<Landing />} />
                <Route path="/register" element={<Register />} />
                <Route path="/login" element={<Login />} />
                <Route path="/quiz" element={<Quiz />} />
                {isAdmin && <Route path="/admin/dashboard" element={<AdminDashboard />} />} {/* Admin dashboard route */}
            </Routes>
        </div>
    );
}

export default function AppWrapper() {
    return (
        <Router>  {/* Wrapping the App component in Router */}
            <App />
        </Router>
    );
}
